import './1_mulligan.test.js';
import './2_turn1.test.js';
import './3_turns2plus.test.js';

describe('Cardfight!! Vanguard Game Engine Test Suite', () => {
    it('should run all test suites sequentially', (done) => {
        // Mocha runs tests within a file sequentially by default.
        // By importing the test files, we ensure they are loaded and executed by the Mocha runner.
        console.log("All test suites have been loaded. Mocha will now run them.");
        done();
    });
});

