import { expect } from 'chai';
import { setupTestGame } from './helpers.js';
import { getPossibleActions } from '../src/ActionManager.js';

describe('Step 1: Mulligan Phase', () => {
    let party, playground1, playground2;

    before(() => {
        // Setup a game state before tests run
        const initial_state = setupTestGame();
        party = initial_state.party;
        playground1 = initial_state.playground1;
        playground2 = initial_state.playground2;
    });

    it('should present 32 (2^5) mulligan options to each player', () => {
        party.startGame(); // This will do the initial setup (shuffle, draw) and set phase to 'mulligan'

        const player1_mulligan_actions = getPossibleActions(party);
        expect(player1_mulligan_actions).to.have.lengthOf(32);
    });
});

