import Card from '../src/Card.js';
import Playground from '../src/Playground.js';
import Party from '../src/Party.js';

/**
 * Creates a mock card.
 * @param {object} props - Card properties.
 * @returns {Card}
 */
export function createCard(props) {
    return new Card({
        id: Math.random().toString(36).substr(2, 9),
        name: 'Test Card',
        grade: 1,
        power: 8000,
        ...props,
    });
}

/**
 * Sets up a fresh game state for testing.
 * @returns {{party: Party, playground1: Playground, playground2: Playground}}
 */
export function setupTestGame() {
    // Create mock decks
    const rideDeckCard1 = createCard({ name: 'Vanguard Starter P1', grade: 0, power: 5000 });
    const rideDeckCard2 = createCard({ name: 'Vanguard Starter P2', grade: 0, power: 5000 });
    const mainDeck1 = Array.from({ length: 50 }, (_, i) => createCard({ name: `Unit ${i}`, grade: (i % 4) }));
    const mainDeck2 = Array.from({ length: 50 }, (_, i) => createCard({ name: `Unit ${i}`, grade: (i % 4) }));

    const playground1 = new Playground(mainDeck1, [rideDeckCard1]);
    const playground2 = new Playground(mainDeck2, [rideDeckCard2]);

    // Place initial vanguard as per rules
    playground1.board.getCircle('V').unit = playground1.rideDeck[0];
    playground2.board.getCircle('V').unit = playground2.rideDeck[0];
    playground1.draw(5);
    playground2.draw(5);

    // Party will manage the two players
    const party = new Party(playground1, playground2);

    // The game state needs to be cloned for each action to test different branches.
    // For now, we'll return the initial setup.
    
    return { party, playground1, playground2 };
}

