import Board from './Board.js';

export default class Playground {
    constructor(mainDeck = [], rideDeck = []) {
        // this.name = name; // Name can be managed by Party if needed
        this.board = new Board();

        // Game zones
        this.hand = [];
        this.deck = mainDeck;
        this.rideDeck = rideDeck;
        this.dropZone = [];
        this.damageZone = [];
        this.soul = [];
        this.gZone = []; // For future G-Unit implementation
        this.bindZone = [];
        this.triggerZone = [];
        this.orderZone = [];
    }

    /**
     * Shuffles the main deck.
     */
    shuffleDeck() {
        // Fisher-Yates shuffle algorithm
        for (let i = this.deck.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [this.deck[i], this.deck[j]] = [this.deck[j], this.deck[i]];
        }
    }

    /**
     * Draws a specified number of cards from the deck to the hand.
     * @param {number} count - The number of cards to draw.
     * @returns {Card[]} The cards that were drawn.
     */
    draw(count = 1) {
        const drawnCards = [];
        for (let i = 0; i < count; i++) {
            if (this.deck.length > 0) {
                const card = this.deck.pop();
                this.hand.push(card);
                drawnCards.push(card);
            } else {
                console.warn(`A player tried to draw but their deck is empty.`);
            }
        }
        return drawnCards;
    }
}

