sourcesContent:
class Circle {
    constructor(tag) {
        this.tag = tag;
        this.unit = null;
        this.isLocked = false;
    }
}

export default class Board {
    constructor() {
        // R1, V, R2
        this.frontRow = [new Circle('R1'), new Circle('V'), new Circle('R2')];
        // R3, R4, R5
        this.backRow = [new Circle('R3'), new Circle('R4'), new Circle('R5')];
    }
    
    getCircle(tag) {
        return [...this.frontRow, ...this.backRow].find(c => c.tag === tag);
    }
}

