import { getPossibleActions } from './ActionManager.js';
import { applyAction } from './ActionApplier.js';

/**
 * Manages the game flow, turns, and history between two playgrounds.
 */
class Party {
  constructor(playground1, playground2) {
    this.playgrounds = [playground1, playground2];
    this.turn = 0;
    this.currentPlayerIndex = 0; // 0 for playground1, 1 for playground2
    this.phase = 'setup'; // e.g., setup, mulligan, stand, draw, ride, main, battle, end
    this.history = []; // To store actions taken
  }

  startGame() {
    console.log("\n--- Starting Game ---");
    this.turn = 1;
    this.currentPlayerIndex = 0;
    this.phase = 'mulligan';

    // Initial setup based on rules
    this.playgrounds.forEach(pg => {
        // Place initial vanguard as per rules
        if (pg.rideDeck.length > 0) {
            pg.board.getCircle('V').unit = pg.rideDeck[0];
        }
        pg.shuffleDeck();
        pg.draw(5); // Initial draw
    });
  }

  runTurn() {
    // This method will now manage the entire turn flow, phase by phase
    // It will generate actions, choose one (for now, the first one), and apply it.

    // Mulligan Phase (handled by applyMulligan, which transitions to next player's mulligan or stand phase)
    // Stand Phase
    // Draw Phase
    // Ride Phase
    // Main Phase
    // Battle Phase
    // End Phase

    // For now, let's simulate a basic flow for the first few phases
    // The actual AI logic for choosing actions will go here later.

    let currentGameState = this; // Start with the current Party instance as the game state

    // Loop until the phase is no longer 'mulligan' or 'setup'
    while (currentGameState.phase === 'mulligan' || currentGameState.phase === 'setup') {
        const possibleActions = getPossibleActions(currentGameState);
        if (possibleActions.length > 0) {
            // For now, just pick the first action (e.g., keep all cards for mulligan)
            const chosenAction = possibleActions[0];
            currentGameState = applyAction(currentGameState, chosenAction);
            // Update the Party instance with the new state
            Object.assign(this, currentGameState);
        } else {
            console.warn("No possible actions during mulligan/setup phase. This shouldn't happen.");
            break;
        }
    }

    // After mulligan, the phase should be 'stand' for Player 1
    if (this.phase === 'stand') {
        console.log(`\n==== TURN ${this.turn} | PLAYER ${this.currentPlayerIndex + 1} | PHASE: STAND ====`);
        const activePlayground = this.playgrounds[this.currentPlayerIndex];
        // Stand all rested units
        activePlayground.board.frontRow.forEach(circle => {
            if (circle.unit) circle.unit.isResting = false;
        });
        activePlayground.board.backRow.forEach(circle => {
            if (circle.unit) circle.unit.isResting = false;
        });
        this.phase = 'draw';
    }

    if (this.phase === 'draw') {
        console.log(`\n==== TURN ${this.turn} | PLAYER ${this.currentPlayerIndex + 1} | PHASE: DRAW ====`);
        const activePlayground = this.playgrounds[this.currentPlayerIndex];
        activePlayground.draw(1); // Draw 1 card
        this.phase = 'ride';
    }

    if (this.phase === 'ride') {
        console.log(`\n==== TURN ${this.turn} | PLAYER ${this.currentPlayerIndex + 1} | PHASE: RIDE ====`);
        const possibleActions = getPossibleActions(this);
        if (possibleActions.length > 0) {
            // For now, just pick the first action (e.g., the first ride option or pass)
            const chosenAction = possibleActions[0];
            const newGameState = applyAction(this, chosenAction);
            Object.assign(this, newGameState); // Update the Party instance with the new state
        } else {
            console.warn("No possible actions during ride phase. This shouldn't happen.");
            this.phase = 'main'; // Fallback to main phase
        }
    }

    if (this.phase === 'main') {
        console.log(`\n==== TURN ${this.turn} | PLAYER ${this.currentPlayerIndex + 1} | PHASE: MAIN ====`);
        // Main phase actions are handled by the main loop in index.js for now
    }
  }

  nextTurn() {
    this.turn++;
    this.currentPlayerIndex = 1 - this.currentPlayerIndex; // Switch player
  }

  isGameOver() {
    // Game over conditions: a player has 6 or more damage, or decks out.
    // Or a turn limit for the simulation.
    return this.turn > 6 || this.playgrounds.some(p => p.damageZone.length >= 6);
  }
}

export default Party;

