import { expect } from 'chai';
import { setupTestGame } from './helpers.js';
import { getPossibleActions } from '../src/ActionManager.js';

describe('Step 2: Turn 1 (First Player)', () => {
    let party, playground1, playground2;

    before(() => {
        // This setup should represent the state AFTER mulligan
        const initial_state = setupTestGame();
        party = initial_state.party;

        // Simulate starting the first turn and getting past mulligan
        party.startGame();
        // Run turn twice to get past both players' mulligan and into player 1's stand phase
        party = party.runTurn(); // Player 1 mulligan
        party = party.runTurn(); // Player 2 mulligan, then Player 1 stand phase
    });

    it('Stand Phase: should have no rested units', () => {
        // At the start of the game, no units are rested. This is a sanity check.
        const restedUnits = party.playgrounds[0].board.frontRow.filter(u => u.unit && u.unit.isResting)
            .concat(party.playgrounds[0].board.backRow.filter(u => u.unit && u.unit.isResting));
        expect(restedUnits).to.be.empty;
    });

    it('Draw Phase: player should draw 1 card and have 6 cards in hand', () => {
        // At this point, party.phase should be 'stand'
        console.log('Before draw phase runTurn - Player 1 Hand:', party.playgrounds[0].hand.length);
        party = party.runTurn(); // This will execute draw phase and transition to ride phase
        console.log('After draw phase runTurn - Player 1 Hand:', party.playgrounds[0].hand.length);
        
        expect(party.playgrounds[0].hand).to.have.lengthOf(6);
    });

    it('Main Phase: should display possible CALL actions', () => {
        // Set the game to the main phase for the test
        party.phase = 'main';

        const actions = getPossibleActions(party);

        console.log('--- Possible Actions for Player 1 ---');
        console.log(JSON.stringify(actions, null, 2));

        expect(actions).to.be.an('array');
        expect(actions.some(a => a.type === 'PASS_MAIN_PHASE')).to.be.true;
