import { getPossibleActions } from './ActionManager.js';
import { applyAction } from './ActionApplier.js';

/**
 * Manages the game flow, turns, and history between two playgrounds.
 */
class Party {
  constructor(playground1, playground2) {
    this.playgrounds = [playground1, playground2];
    this.turn = 0;
    this.currentPlayerIndex = 0; // 0 for playground1, 1 for playground2
    this.phase = 'setup'; // e.g., setup, mulligan, stand, draw, ride, main, battle, end
    this.history = []; // To store actions taken
  }

  startGame() {
    console.log("\n--- Starting Game ---");
    this.turn = 1;
    this.currentPlayerIndex = 0;
    this.phase = 'mulligan';

    // Initial setup based on rules
    this.playgrounds.forEach(pg => {
        // Place initial vanguard as per rules
        if (pg.rideDeck.length > 0) {
            pg.board.getCircle('V').unit = pg.rideDeck[0];
        }
        pg.shuffleDeck();
        pg.draw(5); // Initial draw
    });
  }

  runTurn() {
    let newGameState = cloneDeep(this); // Start with a clone of the current Party instance

    // Loop until the phase is no longer 'mulligan' or 'setup'
    while (newGameState.phase === 'mulligan' || newGameState.phase === 'setup') {
        const possibleActions = getPossibleActions(newGameState);
        if (possibleActions.length > 0) {
            const chosenAction = possibleActions[0];
            newGameState = applyAction(newGameState, chosenAction);
        } else {
            console.warn("No possible actions during mulligan/setup phase. This shouldn't happen.");
            break;
        }
    }

    // After mulligan, the phase should be 'stand' for Player 1
    if (newGameState.phase === 'stand') {
